import 'i18next';

import en from '../public/locales/en.json';

declare module 'i18next' {
  // Extend CustomTypeOptions
  interface CustomTypeOptions {
    // Setting Default Namespace As English
    defaultNS: 'en';
    // Custom Types For Resources
    resources: {
      en: typeof en;
    };
    // Never Return Null
    returnNull: false;
  }
}
